import React, { Component } from 'react'
import Events from './Events'

export default class Tournaments extends Component {

    constructor(props){
        super(props);
        this.state={tournaments:[],events:[],sport:''}
    }

    componentDidMount(){
        console.log("i am mounted");
        const url = "http://www.localhost:8000/tennis/";
        fetch(url, {
            // mode: 'no-cors',
            method: 'GET',
            headers: {
              Accept: 'application/json',
            },
          },
        )
        .then(response => {
        if (response.ok) {
        response.json().then(json => {
        console.log(json);
        this.setState({events:json});
        const tournamentItems= [...new Set(json.map(item => item.event_name))];
        this.setState({tournaments:tournamentItems})
        });
        }
        });
    }

    getEvents(props) {
        let filteredEvents = this.state.events.filter(function (event) {
            return event.event_name === e.target.tournamentName ;
          });
          return filteredEvents;

    }


    render() {    
        const tournamentDivItems=this.state.tournaments.map(item => <div><div className="tournament_div"><p className="tournament_name">{item}</p></div><Events tournamentName={item} events={this.getEvents}/></div>);
        return <div>{tournamentDivItems}</div>;
    }

}
