import React from 'react';
import Tournaments from './components/Tournaments'
import SportIcon from './components/SportIcon';

export default class App extends React.Component {


  constructor(props){
    this.state={sport:'football'};
  }




  changeActiveTab = (sport) => {
    this.setState({sport:sport});
  }

  render() {
    let sport=this.state.sport;
    return (
      <div>
        <SportIcon sport="soccer" activeSport={sport} changeSport={this.changeActiveTab.bind(this,'soccer')}/>
        <SportIcon sport="tennis" activeSport={sport} changeSport={this.changeActiveTab.bind(this,'tennis')}/>
        <SportIcon sport="basketball" activeSport={sport} changeSport={this.changeActiveTab.bind(this,'basketball')}/>
        <SportIcon sport="cricket" activeSport={sport} changeSport={this.changeActiveTab.bind(this,'cricket')}/>
        <SportIcon sport="baseball" activeSport={sport} changeSport={this.changeActiveTab.bind(this,'baseball')}/>
        <SportIcon sport="hockey" activeSport={sport} changeSport={this.changeActiveTab.bind(this,'hockey')}/>
        <SportIcon sport="american_football" activeSport={sport} changeSport={this.changeActiveTab.bind(this,'american_football')}/>  
      </div>
    )
  }
}
