import React from 'react';
import Tournaments from './components/Tournaments'
import SportIcon from './components/SportIcon';

function App() {

  state={sport:'football'};

  let sport=this.state.sport;

  changeActiveTab = (sport) => {
    this.setState({sport:sport});
  }




  return (
    <div className="App">
      <div>
      <SportIcon sport="soccer" activeSport={sport} changeSport="changeActiveTab.bind(this,'soccer')"/>
      <SportIcon sport="tennis" activeSport={sport} changeSport="changeActiveTab.bind(this,'tennis')"/>
      <SportIcon sport="basketball" activeSport={sport} changeSport="changeActiveTab.bind(this,'basketball')"/>
      <SportIcon sport="cricket" activeSport={sport} changeSport="changeActiveTab.bind(this,'cricket')"/>
      <SportIcon sport="baseball" activeSport={sport} changeSport="changeActiveTab.bind(this,'baseball')"/>
      <SportIcon sport="hockey" activeSport={sport} changeSport="changeActiveTab.bind(this,'hockey')"/>
      <SportIcon sport="american_football" activeSport={sport} changeSport="changeActiveTab.bind(this,'american_football')"/>
      </div>
      <Tournaments />
    </div>
  );
}

export default App;
