import React, { Component } from 'react'

export default class SportIcon extends Component {

    constructor(props) {
        super(props);
        this.state = {
            active: false,
        };
        if(this.props.activeSport===this.props.sport){
            this.setState({active:true})
        }
    }


    componentDidUpdate(){
        if(this.props.activeSport===this.props.sport){
            this.setState({active:true})
        }
        else{
            this.setState({active:false});
        }
    }

    render() {
        return (
            <div class="icon_div">
                <button className={this.state.active ? 'btn sport_icon selected':'btn sport_icon' } onClick={this.props.changeSport}><img src={`./images/${this.props.sport}.svg`} style={{width:'50px',height:'40px'}}/></button>
            </div>
        )
    }
}
