import React from 'react';
import SportIcon from './SportIcon';

export default class SportIcons extends React.Component {


  constructor(props){
    super(props);
    this.state={sport:'soccer'};
  }




  changeActiveTab = (sport) => {
    this.setState({sport:sport});
  }

  render() {
    let sport=this.state.sport;
    let icons=['soccer','tennis','basketball','cricket','baseball','hockey','american_football'];
    const iconContent=icons.map( icon => <SportIcon sport={icon} activeSport={sport} changeSport={this.changeActiveTab.bind(this,{icon})}/>);
    return (
      <div>
        {iconContent}
      </div>
    )
  }
}
