import React, { Component } from 'react'

export default class SportIcon extends Component {

    constructor(props) {
        super(props);
        this.state = {
            active: false,
        };
        if(this.props.selected){
            this.setState({active:true})
        }
    }
    toggleClass= () => {
        const currentState = this.state.active;
        this.setState({ active: !currentState });

    };

    render() {
        return (
            <div class="inline-block">
                <button className={this.state.active ? 'btn sport_icon selected':'btn sport_icon' } onClick={this.toggleClass}><img src={`./images/${this.props.sport}.svg`} style={{width:'50px',height:'40px'}}/></button>
            </div>
        )
    }
}
