import React from 'react';
import SportIcon from './SportIcon';

export default class SportIcons extends React.Component {


  constructor(props){
    super(props);
  }




  changeActiveTab = (sportName) => {
    this.setState({sport:sportName});
    console.log(this.state.sport);
  }

  render() {
    let sport=this.props.currentSport;
    let icons=['soccer','tennis','basketball','cricket','baseball','hockey','american_football'];
    let iconContent=icons.map( icon => <SportIcon sport={icon} activeSport={sport} changeSport={this.props.fetchData}/>);
    return (
      <div>
        {iconContent}
      </div>
    )
  }
}
