import React, { Component } from 'react'
import images from './';

export default class SportIcon extends Component {

    constructor(props) {
        super(props);
        this.state = {
            active: false,
        };
        if(this.props.selected){
            this.setState({active:true})
        }
    }
    toggleClass= () => {
        const currentState = this.state.active;
        this.setState({ active: !currentState });

    };

    render() {
        var sport=this.props.sport;
        console.log(sport);
        return (
            <div class="inline-block">
                <button className={this.state.active ? 'btn sport_icon selected':'btn sport_icon' } onClick={this.toggleClass}><Image source={images.profile.football} /></button>
            </div>
        )
    }
}
