import React, { Component } from 'react'

export default class SportIcon extends Component {

    constructor(props) {
        super(props);
        this.state = {
            active: false,
        };
        if(this.props.selected){
            this.setState({active:true})
        }
    }
    toggleClass() {
        const currentState = this.state.active;
        this.setState({ active: !currentState });

    };

    render() {
        const sport=this.props.sport;
        return (
            <div class="inline-block">
                <button className={this.state.active ? 'btn sport_icon selected':'btn sport_icon' } onClick={this.toggleClass}><img src={require('./images/{sport}.svg')} style={{height:'50px',width:'40px'}}></img></button>
            </div>
        )
    }
}
